/**
 * 属性ID
 *
 * Posture#attrs 属性配列に格納された値にアクセスするための添字として用いる。
 * 例えばX方向移動量にアクセスするには posture.attrs[AttrId.tx] とする。
 */
declare enum AttrId {
    /**
     * X方向移動。
     */
    tx = 0,
    /**
     * Y方向移動。
     */
    ty = 1,
    /**
     * Z軸回転(deg)。
     */
    rz = 2,
    /**
     * X方向スケール。
     */
    sx = 3,
    /**
     * Y方向スケール。
     */
    sy = 4,
    /**
     * 半透明度。
     *
     * 0で透明、1で不透明を表す。
     */
    alpha = 5,
    /**
     * セル値。
     *
     * CellValueクラスを参照。
     */
    cv = 6,
    /**
     * セル回転位置X座標。
     *
     * セルの中心位置は pvtx, pvty 属性によって与えられる。
     */
    pvtx = 7,
    /**
     * セル回転位置Y座標。
     *
     * セルの中心位置は pvtx, pvty 属性によって与えられる。
     */
    pvty = 8,
    /**
     * セルテクスチャU座標。
     *
     * セルの参照する画像上の参照位置。正規化されている。
     */
    tu = 9,
    /**
     * セルテクスチャV座標。
     *
     * セルの参照する画像上の参照位置。正規化されている。
     */
    tv = 10,
    /**
     * セル描画優先順位。
     *
     * あるActorをレンダリングするときのセル間の描画優先順位。
     */
    prio = 11,
    /**
     * 可視属性。
     */
    visibility = 12,
    /**
     * 円アタリ判定半径。
     */
    ccr = 13,
    /**
     * 水平フリップ。
     */
    flipH = 14,
    /**
     * 垂直フリップ。
     */
    flipV = 15,
    /**
     * ユーザデータ。
     */
    userData = 16,
}
export = AttrId;
