import{_ as i,c as a,o as e,a2 as r}from"./chunks/framework.CIiVzG-O.js";const s="/assets/tbl-a-1-size.Ds2U4QAv.png",l="/assets/tbl-a-1-ratio.L_A9LmiH.png",n="/assets/tbl-a-2-size.BPsD0ZlH.png",o="/assets/tbl-a-2-ratio.CMAP_4rP.png",d="/assets/tbl-a-3-ok.ni8ZJCPG.png",g="/assets/tbl-a-3-ng.DvNdxda5.png",h="/assets/tbl-b-size.BN2EMfrH.png",b="/assets/tbl-b-ratio.nI6jbYFZ.png",c="/assets/a-b-summary.O6_dSH31.png",p="/assets/tbl-c-size-ng.BZ9SvrI4.png",u="/assets/tbl-c-ratio-ng.Br58trWC.png",m="/assets/tbl-c-size-ok.BxXdXfK5.png",_="/assets/tbl-c-ratio-ok.DUwq1XTJ.png",U=JSON.parse('{"title":"デザインガイドライン","description":"","frontmatter":{},"headers":[],"relativePath":"shin-ichiba/design-guidelines.md","filePath":"shin-ichiba/design-guidelines.md"}'),f={name:"shin-ichiba/design-guidelines.md"};function k(I,t,P,q,w,x){return e(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="デザインガイドライン" tabindex="-1">デザインガイドライン <a class="header-anchor" href="#デザインガイドライン" aria-label="Permalink to &quot;デザインガイドライン&quot;">​</a></h1><h2 id="about" tabindex="-1">本ガイドラインについて <a class="header-anchor" href="#about" aria-label="Permalink to &quot;本ガイドラインについて {#about}&quot;">​</a></h2><ul><li>「つりっくま」等のニコ生ゲームや、「おしゃべりトピック」等の放送ネタの開発をおこなう際、 <ul><li>PC やスマートフォンアプリのプレイヤー上の UI とコンテンツ操作が衝突する</li><li>スマートフォンアプリでのポートレト表示において画面が小さすぎて操作しづらい..等の理由で、ユーザーが満足な操作をおこなえないことがあります</li></ul></li><li>より多くのユーザーが満足できるコンテンツづくりを開発者の皆様におこなっていただけるよう本ガイドラインを作成しました</li><li>なお、本資料で提示するのは、<u><strong>クリックやタップ、スワイプ操作の必要な「ボタン」やその UI のガイドライン</strong></u>となります <ul><li><u>クリックやタップ操作の無い UI や、その他のグラフィックに関しては、本資料の記載事項を考慮する必要はございません</u></li></ul></li></ul><h2 id="dont-place-ui-area" tabindex="-1">A：ボタン等の設置を避けるべきエリア <a class="header-anchor" href="#dont-place-ui-area" aria-label="Permalink to &quot;A：ボタン等の設置を避けるべきエリア {#dont-place-ui-area}&quot;">​</a></h2><p>本項で説明するエリアに、クリックやタップが必要なボタン等を設置することは、PC・スマートフォンアプリのプレイヤー UI と競合し、ユーザー操作をおこなえなくなる可能性があります。ここにボタン等を設置することは避けることを推奨します。</p><table border="1" style="width:100%;"><tr><td>A-1.避けること<span style="color:red;">強く</span>推奨</td><td style="vertical-align:top;"><br><br><a href="../../img/shin-ichiba/design/tbl-a-1-size.png" target="_blank"><img src="'+s+'" width="216" height="126"></a><br><br><br><br><a href="../../img/shin-ichiba/design/tbl-a-1-ratio.png" target="_blank"><img src="'+l+'" width="216" height="126"></a></td></tr><tr><td>A-2.避けることを推奨</td><td><br><br><a href="../../img/shin-ichiba/design/tbl-a-2-size.png" target="_blank"><img src="'+n+'" width="216" height="126"></a><br><br><a href="../../img/shin-ichiba/design/tbl-a-2-ratio.png" target="_blank"><img src="'+o+'" width="216" height="126"></a></td></tr></table><h3 id="本エリアにボタン等の設置を検討する場合、下記をご確認ください" tabindex="-1">本エリアにボタン等の設置を検討する場合、下記をご確認ください <a class="header-anchor" href="#本エリアにボタン等の設置を検討する場合、下記をご確認ください" aria-label="Permalink to &quot;本エリアにボタン等の設置を検討する場合、下記をご確認ください&quot;">​</a></h3><ul><li>1.ユーザーの主たるコンテンツ操作と衝突しないか？ <ul><li>画面全体がクリック/タップできるコンテンツ　 → 　エリア外をタップすれば済むため、問題ありません</li><li>コンテンツのメイン操作ではなく、補助的なボタン（オプション/設定など）の設置である　 → 　推奨はしませんが、コンテンツ制作者でご判断ください。</li></ul></li><li>2.本エリア外に十分なクリック/タップ領域が確保されているか？ <ul><li>下記表をご確認ください。</li></ul></li></ul><table border="1"><tr><td>問題なし</td><td><a href="../../img/shin-ichiba/design/tbl-a-3-ok.png" target="_blank"><img src="'+d+'" width="216" height="126"></a></td><td><p>本エリア外に半分以上、クリック/タップ領域が確保されている</p></td></tr><tr><td><span style="color:red;">問題あり</span></td><td><a href="../../img/shin-ichiba/design/tbl-a-3-ng.png" target="_blank"><img src="'+g+'" width="216" height="126"></a></td><td><span style="color:red;">本エリア外に半分以上、クリック/タップ領域が確保されていない</span></td></tr></table><h2 id="avoid-to-swipe-area" tabindex="-1">B：スワイプ操作を避けるべき（且つ、可能であればボタンの設置も避けるべき）エリア <a class="header-anchor" href="#avoid-to-swipe-area" aria-label="Permalink to &quot;B：スワイプ操作を避けるべき（且つ、可能であればボタンの設置も避けるべき）エリア {#avoid-to-swipe-area}&quot;">​</a></h2><p>本項で説明するエリアでスワイプ操作をおこなわせるのは、スマートフォンアプリのプレイヤー UI と競合し、ユーザーが操作をおこなえなくなる可能性が高いです。ここでスワイプ操作をおこなわせるのは避けることを**<span style="color:red;">強く</span>推奨**します。</p><table border="1"><tr><td style="padding:5px;"></td><td style="padding:5px;vertical-align:top;"><p>下記のUIと競合します</p></td></tr><tr><td><a href="../../img/shin-ichiba/design/tbl-b-size.png" target="_blank"><img src="'+h+'" width="216" height="126"></a><a href="../../img/shin-ichiba/design/tbl-b-ratio.png" target="_blank"><img src="'+b+'" width="216" height="126"></a></td><td style="vertical-align:top;text-align:left;"><p><strong>iOS：フルスクリーン時OS標準UI</strong><br> 1.右上：コントローラーセンター<br> 2.左下：通知<br> 3.下部：ホームに戻る<br> 4.左右：セーフエリア外（マージンエリア）<br><br><br><strong>Android：フルスクリーン時OS標準UI<br></strong> 1.上部：ステータスバー </p></td></tr></table><h3 id="本エリアでスワイプ操作-ボタン等の設置を検討する場合、下記をご確認ください" tabindex="-1">本エリアでスワイプ操作/ボタン等の設置を検討する場合、下記をご確認ください <a class="header-anchor" href="#本エリアでスワイプ操作-ボタン等の設置を検討する場合、下記をご確認ください" aria-label="Permalink to &quot;本エリアでスワイプ操作/ボタン等の設置を検討する場合、下記をご確認ください&quot;">​</a></h3><ul><li>ユーザーの主たるコンテンツ操作と衝突しないか？ <ul><li>画面全体をスワイプ操作するコンテンツ　 → 　問題ありません</li></ul></li></ul><h2 id="conclusion-a-and-b" tabindex="-1">A、B のまとめ <a class="header-anchor" href="#conclusion-a-and-b" aria-label="Permalink to &quot;A、B のまとめ {#conclusion-a-and-b}&quot;">​</a></h2><ul><li>非推奨領域を表した透過 png を制作しましたので、こちらのデザインに当てはめご利用ください <ul><li>基本的には、白地のエリアに、クリックやタップの必要な「ボタン」やその他「UI」の操作をおさえることを推奨します</li><li>白地以外での操作をおこなう場合は、上記ガイドラインをご参照のうえ、ユーザー操作が極力快適におこなえるよう、デザイン調整をご検討ください</li></ul></li></ul><a href="../../img/shin-ichiba/design/a-b-summary.png" target="_blank"><img src="'+c+'"></a><ul><li><strong>赤色...UI 設置を避けることを強く推奨</strong></li><li><strong>黄色...UI 設置を避けることを推奨</strong></li><li><strong>オレンジ色...スワイプ操作を避けることを強く推奨</strong></li></ul><h2 id="min-ui-size" tabindex="-1">C：UI の最小サイズ <a class="header-anchor" href="#min-ui-size" aria-label="Permalink to &quot;C：UI の最小サイズ {#min-ui-size}&quot;">​</a></h2><p>コンテンツ上に設置されるボタン等の UI のタップ範囲が小さいと、スマートフォンアプリのポートレイト視聴時にタップしづらいなど問題が出ることがあります。下記を参考に作成してください。</p><table border="1"><tr><td style="vertical-align:top;"><p><span style="color:red;">下記未満のサイズは推奨しません</span></p></td><td style="vertical-align:top;"><p>可能であれば、下記程度のサイズを確保してください</p></td></tr><tr><td style=""><a href="../../img/shin-ichiba/design/tbl-c-size-ng.png" target="_blank"><img src="'+p+'" width="216" height="126"></a><br><a href="../../img/shin-ichiba/design/tbl-c-ratio-ng.png" target="_blank"><img src="'+u+'" width="216" height="126"></a></td><td style="vertical-align:top;"><a href="../../img/shin-ichiba/design/tbl-c-size-ok.png" target="_blank"><img src="'+m+'" width="216" height="126"></a><br><a href="../../img/shin-ichiba/design/tbl-c-ratio-ok.png" target="_blank"><img src="'+_+'" width="216" height="126"></a></td></tr></table>',21)])])}const v=i(f,[["render",k]]);export{U as __pageData,v as default};
