/**
 * 定期実行処理。
 * 自身に紐付けられた関数を定期的に呼び出すだけの型。
 */
export interface Looper {
    /**
     * 定期実行の開始。
     * 自身に紐付けられた関数を任意の頻度で呼び出す。
     */
    start(): void;
    /**
     * 定期実行の停止。
     * `start()` で開始された定期実行を停止する。`start()` が呼び出されていない時の動作結果は不定である。
     * このメソッドは、自身に紐付けられた関数の実行中に呼び出された場合でも正しく動作せねばならない。
     */
    stop(): void;
}
